void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    fragColor = texture(iChannel0,fragCoord/iResolution.xy).rgba;
}

#define s1 sin(iTime*5.0)
#define c1 cos(iTime*5.0)
#define s2 sin((iTime+(iTime/float(iFrame))/4.0)*5.0)
#define c2 cos((iTime+(iTime/float(iFrame))/4.0)*5.0)
#define s3 sin((iTime+2.0*(iTime/float(iFrame))/4.0)*5.0)
#define c3 cos((iTime+2.0*(iTime/float(iFrame))/4.0)*5.0)
#define s4 sin((iTime+3.0*(iTime/float(iFrame))/4.0)*5.0)
#define c4 cos((iTime+3.0*(iTime/float(iFrame))/4.0)*5.0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float col = 0.0;
    vec2 uv = (fragCoord-(iResolution.xy/2.0))/iResolution.y;
    vec2 off1 = uv - vec2(s1, c1)/vec2(2.0,5.0);
    vec2 off2 = uv - vec2(s2, c2)/vec2(2.0,5.0);
    vec2 off3 = uv - vec2(s3, c3)/vec2(2.0,5.0);
    vec2 off4 = uv - vec2(s4, c4)/vec2(2.0,5.0);
    if(dot(off1, off1)<0.1){
        col += 0.25;
    }
    if(dot(off2, off2)<0.1){
        col += 0.25;
    }
    if(dot(off3, off3)<0.1){
        col += 0.25;
    }
    if(dot(off4, off4)<0.1){
        col += 0.25;
    }
    col = (col+texture(iChannel0,fragCoord/iResolution.xy).r)/2.0;
    fragColor = vec4(col,col,col,1.0);
}
