precision highp float;
const float PI = 3.1415926;
// random function
float rand(float n) {
	// fract(x) return the decimal of x
	return fract(sin(n) * 43758.5453123);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float maxJitter = 0.01;
    float duration  = 1.0;
    float colorROffset = 0.01;
    float colorGOffset = -0.02;
    float colorBOffset = -0.035;
    float time = iTime;
    float currentTime = mod(time, duration*2.0); // [0, 1]
    float amplitude = max( sin( currentTime*(PI/duration) ) , 0.0); // [0, 1]
    float jitter = rand(uv.y) * 2.0 - 1.0; // [-1, 1]
    bool needOffset = abs(jitter) < maxJitter * amplitude;

    float textureX = uv.x + (needOffset ? jitter : (jitter * amplitude * 0.006));
    vec2 textureCoords = vec2(textureX, uv.y);

    vec4 mask = texture(iChannel0, textureCoords);
    vec4 maskR = texture(iChannel0, textureCoords + vec2(colorROffset * amplitude, 0.0));
    vec4 maskG = texture(iChannel0, textureCoords + vec2(colorGOffset * amplitude, 0.0));
    vec4 maskB = texture(iChannel0, textureCoords + vec2(colorBOffset * amplitude, 0.0));
    fragColor = vec4(maskR.r, maskG.g, maskB.b, mask.a);
}
