void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv0 = (fragCoord + vec2(0,-4))/iResolution.xy;
    vec2 uv1 = (fragCoord + vec2(0,-3))/iResolution.xy;
    vec2 uv2 = (fragCoord + vec2(0,-2))/iResolution.xy;
    vec2 uv3 = (fragCoord + vec2(0,-2))/iResolution.xy;
    vec2 uv4 = (fragCoord + vec2(0,0))/iResolution.xy;
    vec2 uv5 = (fragCoord + vec2(0,1))/iResolution.xy;
    vec2 uv6 = (fragCoord + vec2(0,2))/iResolution.xy;
    vec2 uv7 = (fragCoord + vec2(0,3))/iResolution.xy;
    vec2 uv8 = (fragCoord + vec2(0,4))/iResolution.xy;

    // Output to screen
    fragColor = (texture(iChannel0, uv0) +
                 texture(iChannel0, uv1) + 
                 texture(iChannel0, uv2) + 
                 texture(iChannel0, uv3) + 
                 texture(iChannel0, uv4) + 
                 texture(iChannel0, uv5) + 
                 texture(iChannel0, uv6) + 
                 texture(iChannel0, uv7) + 
                 texture(iChannel0, uv8))
        		 / 9.0;
}

