void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 c1 = vec4(0., 0., 0., 1.);
    vec4 c2 = vec4(1., 1., 1., 1.);
    if ((int(iTime) % 2) == 0)
    {
        vec4 t = c1;
        c1 = c2;
        c2 = t;
    }
    int x = int(fragCoord.x);
    int y = int(fragCoord.y);
    if (((x % 20) < 10) && ((y % 20) < 10)) fragColor = c1;
    else fragColor = c2;
}
