vec3 colorA = vec3(1,0.,0.);
vec3 colorB = vec3(0.,0.,1.);
vec3 colorC= vec3 (1.,1.,1.);


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    float value = step(.35,uv.y) - step(.65,uv.y);
    float value2 = step(.65,uv.y) - step(1.,uv.y);

    vec3 col = mix(colorA,colorB,value);
    vec3 col2 =mix(col,colorC,value2);
    // Output to screen
    fragColor = vec4(col2,1.0);
}
