#define DIV_SIZE_X 3.0
#define DIV_SIZE_Y 3.0
#define GRILLE_SIZE 0.0
#define GRILLE_FREQ 4.0
#define THICKNESS 1.0
#define BLOOM true

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    for (float i = -2.0; i <= 2.0; i++)
    {
        float pos = iResolution.y / 2.0 + i / 2.0;

        float freq = (iMouse.x / iResolution.x) * 100.0 + 10.0;

        float amp = iMouse.y / iResolution.y;
        amp = 2.0 * amp - 1.0;

        float xpos = floor((fragCoord.x - iResolution.x / 2.0) / DIV_SIZE_X) * DIV_SIZE_X;

        pos *= sin(xpos / freq + iTime * 2.0) * cos(xpos / (freq * 5.0) + iTime * 2.0) * amp + 1.0;

        pos = floor(pos / DIV_SIZE_Y) * DIV_SIZE_Y;

        float grille = 1.0;
        if (GRILLE_SIZE != 0.0)
            grille = sin(fragCoord.y * GRILLE_FREQ + iTime * 2.0) * GRILLE_SIZE;

        float dist = distance(floor(fragCoord.y / DIV_SIZE_Y) * DIV_SIZE_Y, pos);
        float thickness = (THICKNESS * grille);
        float col = 0.0;

        if (BLOOM) col = thickness / dist;
        else col = thickness - dist;

        fragColor += vec4(vec3(col * (1.5 - i), col, col * (i + 3.0)), 1.0) * 2.0 / (i + 4.0) / 2.0;
    }
}
