// variant of https://shadertoy.com/view/3tyyDw

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;

#define p 4.
#define D(U) ( V = pow( abs(U),vec2(p)), pow(V.x+V.y,1./(p)) )

#define draw smoothstep(.5,0., abs( D(U-.5*R-5.*vec2(sin(iTime),0)) - R.y*.4 ) )

    O = vec4(1);
    vec2 V;
    U-=.5;       O.r -= draw;
    U.x += .333; O.g -= draw;
    U.x += .333; O.b -= draw;
}
