#define PI 3.14159265358979323846

float random (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}

vec2 random2(vec2 st){
    st = vec2( dot(st,vec2(127.1,311.7)),
              dot(st,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}
vec2 rotate2D (vec2 _st, float _angle) {
    _st -= 0.5;
    _st =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _st;
    _st += 0.5;
    return _st;
}

float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    vec2 u = f*f*(3.0-2.0*f) *f;

    return mix( mix( dot( random2(i + vec2(0.0,0.0) ), f - vec2(0.0,0.0) ),
                     dot( random2(i + vec2(1.0,0.0) ), f - vec2(1.0,0.0) ), u.x),
                mix( dot( random2(i + vec2(0.0,1.0) ), f - vec2(0.0,1.0) ),
                     dot( random2(i + vec2(1.0,1.0) ), f - vec2(1.0,1.0) ), u.x), u.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    // Time varying pixel color

    vec3 col = vec3(0.);
    //float time = mod(iTime,7200);
    float t =50.*sin(iTime)/iTime;
    uv += noise(uv * 2.) * t;

    vec3 n = vec3(1.) * smoothstep(.18,.2,noise(uv));
    n += smoothstep(.15,.2,noise(uv * 10.));
    n -= smoothstep(.35,.4,noise(uv * 10.)); 
    //col = 1. - col;
    //n = 1. - n;
    n *= vec3(.71,.94,.84);
    col = mix(vec3(0.332,0.88,0.608),vec3(.67,0.90,0.98),uv.x);
    col +=n;
    // Output to screen
    fragColor = vec4(col,1.0);
}
