void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;
    vec2 st_mouse = fragCoord.xy/iMouse.xy;
    vec3 color =vec3(0.2*abs(sin(iTime)),0.5*abs(sin(iTime)),0.6*abs(sin(iTime)));

    // bottom-left
    vec2 bl = step(vec2(0.1*abs(tan(st_mouse.x))),st);
    float pct = bl.x * bl.y;

    // top-right
    vec2 tr = step(vec2(0.1*abs(sin(st_mouse.y))),1.0-st);
    pct *= tr.x * tr.y;


    color = vec3(pct*color*st_mouse.y*abs(sin(iTime)));

    fragColor = vec4(color,1.0);
}
