void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.yy;

    float r = length(uv);
    float a = atan(uv.x, uv.y);


    float hlIntensity = sin(1.0*(a+iTime*0.79))*0.4+0.4;
    hlIntensity *= exp(hlIntensity);
    hlIntensity *= hlIntensity*0.49;
    hlIntensity *= sin(iTime*1.3)*0.5+0.5;
    vec4 hlColor = vec4(0.7, 0.8, 1.0, 1.0);

    float sna = sin(iTime*0.2);
    float csa = cos(iTime*0.2);
    mat2 rot = mat2(csa, -sna, sna, csa);
    vec4 spaceColor = vec4(1.5, 1.0, 0.05, 1.0) * texture(iChannel0, uv*exp(-r)*((1.0-r)*(1.0-hlIntensity)*0.125)*rot+iTime*0.005);

	float ringRad = 0.33;
    float ringWid = (sin(a+iTime)*0.008+0.017) * (sin(iTime*4.0)*0.07+1.0) + hlIntensity*0.05;
    float ringIntensity = 1.0-smoothstep(0.0,ringWid,abs(ringRad-r));
    vec4 ringColor = mix(vec4(0.0), vec4(1.0,0.7,0.2,1.0), ringIntensity);



    vec4 ringFinal = ringIntensity * mix(ringColor, hlColor, hlIntensity);

    fragColor = r<ringRad ? ringFinal : mix(spaceColor, ringFinal, ringIntensity);
}
