void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-iResolution.xy*.5)/iResolution.y;
    float Time = sin(iTime); 
	uv *=
        mat2(.71+ ((.145 + (0.1*sin(iTime))) * 0.1*iTime ) ,
             -.707 -((.155 + (0.31*cos(iTime+0.2)))* 0.1*iTime), 
             .701+ (((tan(iTime) *0.125) + (0.1*sin(iTime)))* 0.1*iTime), 
             .707+ ((1.105+ (0.1*sin(iTime)))* 0.1*iTime)); 
    uv *= 15.;

    vec2 gv = fract(uv)-.5; 
    vec2 id = floor(uv);


    vec4 mc = vec4(0,0,0,0);
    float t;
    for(float y=-1.; y<=-1.; y++) {
        for(float x=-1.; x<=2.; x++) {
            vec2 offs = vec2(x*1.5f, y );

            t = -(iTime +5.0f)+length(id-offs)* 0.35; 
            float r = mix(.375, .81, sin(t)*0.85);
              float r2 = mix(.88, .35, sin(t + 0.15)*.75); 
            float r3 = mix(.88, 1.05, sin(t + 0.25) *.95);
            float c = smoothstep(r * 1.7, r * 0.9, length(gv+offs));
            mc.x = mc.x*(1.0-c) + c*(1.-mc.x) * 1.3 * ( r2 );
            mc.y = mc.y*(1.-c) + c*(1.-mc.y)* 1.7 * r;
            mc.z = mc.z*(1.-c) + c*(1.-mc.z) * 1.3* r3;
            mc.a = mc.a*(1.-c) + c*(1.-mc.a); 
        } 
    } 

    		mc *= 2.875; 
   mc *= 0.3*mc; 

    fragColor = mc; 

}
