void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 one_color;
    vec4 two_color;
    vec2 coord = fragCoord;


  	vec2 block_size = vec2(20.0,20.0);

    one_color.r = 0.0;
    one_color.g = 0.0;
    one_color.b = 0.0;
    one_color.w = 1.0;

    two_color.r = 0.5;
   	two_color.g = 0.5;
   	two_color.b = 0.5;
    two_color.w = 1.0;

    // if(check_coords(coord,coord.x += 10;


    if (check_coords(coord,block_size))
        one_color = two_color;

	fragColor = one_color; 

}

bool check_coords(vec2 coord,vec2 block_size)
{
    int coord_x = int(coord.x);
    int coord_y = int(coord.y);

    if ( ( coord_x % int(block_size.x) ) == 0 )
        return true;

    if ( ( coord_y % int(block_size.y) ) == 0 )
 		return true;
    return false;
}
