vec3[3] colors = vec3[](vec3(1.), vec3(1., 0., 0.), vec3(0., 1., 0.));

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = (fragCoord * 2. - iResolution.xy) / iResolution.y;
    vec4 a = texture(iChannel0, vec2(fragCoord.x / iResolution.x, 0.));
    float dist = a.z;
    int id = int(a.x);
    fragColor = mix(vec4(colors[id-1], 1.) * a.y, vec4(.25), step(.5, abs(pos.y) * dist));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(fragCoord.y > 4.) return;
    vec4 prot = texelFetch(iChannel0, ivec2(0), 0);
    vec2 rd = vec2((fragCoord.x * 2. - iResolution.x) / iResolution.y, 1.);
    {
        rd = vec2(rd.x*prot.w + rd.y*prot.z, rd.y*prot.w - rd.x*prot.z);
    }
    vec2 nrd = normalize(rd);
    vec2 pos = prot.xy; //starting position
    ivec2 mpos = ivec2(floor(pos)); //starting tile
    vec2 ddxy = 1. / abs(nrd); //stuff
    int side = 0; //even more dda stuff
    ivec2 stxy = ivec2(rd.x > 0. ? 1 : -1, rd.y > 0. ? 1 : -1); //tile stepping direction
    vec2 sdxy = fract(pos); //more stuff
    if(stxy.x > 0) sdxy.x = 1. - sdxy.x;
    if(stxy.y > 0) sdxy.y = 1. - sdxy.y;
    sdxy *= ddxy;
    int tile_hit = 1;
    for(int i = 0; i < 32; i++) {
        if(sdxy.x < sdxy.y) {
        	sdxy.x += ddxy.x;
            mpos.x += stxy.x;
            side = 0;
        } else {
        	sdxy.y += ddxy.y;
            mpos.y += stxy.y;
            side = 1;
        }
        if(is_solid(tile_hit = tile_at(mpos))) break;
    }
    float dist = float(mpos[side]) - pos[side];
    dist += float((1 - stxy[side]) / 2);
    dist /= rd[side];
    //vec2 normal = vec2(0.);
    //normal[side] = float(stxy[side]);
    fragColor = vec4(
        tile_hit,
        float(stxy[side]) * .20 + .80,
        dist,
        0.
    );
}

#define R_LEFT  81
#define R_RIGHT 69
#define LEFT    65
#define RIGHT   68
#define DOWN    83
#define UP      87

#define iTimeDelta (1./60.)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(any(greaterThan(fragCoord, vec2(1.)))) return;
    if(iFrame == 0) {
        fragColor = vec4(8., 8., 1., 0.); return;
    }
    fragColor = texelFetch(iChannel1, ivec2(0.), 0);
    float c = cos(2. * iTimeDelta);
    float s = sin(2. * iTimeDelta);
    if(texelFetch(iChannel0, ivec2(R_RIGHT, 0), 0).x > .5) fragColor.zw *= mat2(c,s,-s,c);
    if(texelFetch(iChannel0, ivec2(R_LEFT , 0), 0).x > .5) fragColor.zw *= mat2(c,-s,s,c);
    fragColor.xy += fragColor.zw * 5. * texelFetch(iChannel0, ivec2(UP   , 0), 0).x * iTimeDelta;
    fragColor.xy -= fragColor.zw * 5. * texelFetch(iChannel0, ivec2(DOWN , 0), 0).x * iTimeDelta;
    fragColor.xy += fragColor.wz * vec2(1., -1.) * 5. * texelFetch(iChannel0, ivec2(RIGHT, 0), 0).x * iTimeDelta;
    fragColor.xy -= fragColor.wz * vec2(1., -1.) * 5. * texelFetch(iChannel0, ivec2(LEFT , 0), 0).x * iTimeDelta;
