#define time iTime
vec2 scroll(vec2 uv,vec2 c) {
    uv.y += sin(c.x+time/6.);
    uv.x += cos(c.y+time/6.);
    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

   vec2 uv  = ( fragCoord -.5* iResolution.xy ) / iResolution.y;

    vec2 c  = floor(4.*uv);
    vec2 cell = fract(4.*scroll(uv+vec2(time/5.,0),c))-0.5;
        cell/=uv.y*2.;
    float color = length(cell)-.2;
    float p = .3;
    color = smoothstep(p,p+.001+abs(c.x/13.),color);
    vec3 fcolor = mix(vec3(0.1),vec3(0.2,0.5,0.6),color);
    fcolor.r *= tan(c.x*5.-time*3.)*3.;

    fragColor = vec4(fcolor,
        1.0);
}
