// Left, regular arithmetic blending. Right, geometric blending.
//
// Geometric blending is to regular blending as the geometric mean is to
// the arithmetic mean. You can also see it like blending in logarithmic
// space.
//
// Geometric blending biases towards the darks. But it's gamma agnostic
// 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 colA = texture( iChannel0, uv ).xyz;
    vec3 colB = texture( iChannel1, uv ).xyz;
    float f = fract(iTime*0.5);

    if( uv.y>0.85 )
    {
        colA = vec3(1.0,0.8,0.2);
        colB = vec3(0.2,0.8,1.0);
        f = fract(uv.x*2.0);
    }

    vec3 col = ( uv.x<0.5 ) ?
           // arithmetic blending (with correct gamma)
           sqrt( colA*colA*(1.0-f) + colB*colB*f ):
           // geometric blending (gamma agnostic)
           pow(colA,vec3(1.0-f)) * pow(colB,vec3(f));

    col *= smoothstep( 0.002, 0.005, abs(uv.x-0.50) );
    col *= smoothstep( 0.002, 0.006, abs(uv.y-0.85) );

    fragColor = vec4(col,1.0);
}
