vec2 rotate2d (vec2 uv, float a) {
	return uv*mat2(sin(a),cos(a),cos(a),-sin(a));
}

vec3 mI (vec2 uv, float time) {

    float c = length(vec2(uv.x+cos(time)*0.1,uv.y+sin(time)*0.1)-0.5);

    //uv = rotate2d(uv-1.,iTime*0.01+sin(uc*2.+time*4.)*0.003);
    //float sp =0.5;
    //uv = abs(mod(uv,sp)-sp/2.)*0.5+time*0.1;
    //uv = fract(uv*1.2+time)-time;
    //uv = mix(fract(abs(uv)+time),uv,sin(time)*0.5+0.5);
    //uv = mix(fract(abs(uv)),uv,sin(time)*0.5+0.5);
    c -= time*.0074;
    //uv = rotate2d(uv-1.,-iTime*c);
    //c*= sin(time*0.1)*100.;
    c*= sin(time*0.01);
    c += sin(uv.x*13.+time*0.1)*cos(uv.y*13.+time*0.1)*4.;
    uv.x -= time*0.002*sin(time*0.4);
    c += sin(uv.x*81.+time*0.1)*cos(uv.y*29.+time*0.1)*0.9;
    uv.xy -= time*0.01*sin(time*0.03);
    c += sin(uv.x*140.+time*0.1)*cos(uv.y*90.+time*0.4)*0.8;
    uv.y += time*0.02*sin(time*0.05);
    c += sin(uv.x*40.+time*0.1)*cos(uv.y*189.+time*0.4)*0.4;
    //c = sin(c*3.-time*26.)+time*0.1;
    c += sin(uv.x*10.+time*0.1)*cos(uv.y*2.+time*0.4)*0.09;
    c = sin(c*3.-time*22.)+time*0.1;
    vec3 col = vec3(sin(c*1.5),sin(c+0.1),cos(c))*0.5+0.5;
    col = mix(col,sin(time*0.4)*texture(iChannel0,mix(col.rg*0.1,uv*0.5,0.9)).rgb,0.2);
	return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv *= 2.;
    float time = iTime*.2;
    float sp =01.01;
    vec2 fv = abs(mod(uv,sp)-sp/2.)*0.5+iTime*0.01;
    vec3 col = mI(fv,time);
    int S = 2;
    float uc = length(uv-1.);
    for (int i=0;i<S;i++) {
        //time *= 0.5;
        uv = rotate2d(uv-1.,1.);
        //sp += 0.5;
        //uv += vec2(1.,-1.);
        fv = abs(mod(uv,sp)-sp/2.)*0.5+iTime*0.01;
    	col += mI(fv, time);
    }
    uv = fragCoord/iResolution.xy;

    float cw = col.r+col.b+col.g;
    col = mix(mI((cw*0.01-((uv*9.)-1.)*0.002*cw)*(1.-uc)*1., time*0.2), col,sin(time*4.)*0.5+0.5);
    col = mix(mI((cw*0.01-((uv*0.2)-1.)*0.01*cw)*(1.-uc)*1., time*0.2), col,sin(time*1.414)*0.5+0.5);
    cw = col.r+col.b+col.g;
    //col = mI(mix(vec2(cw)*0.01+(1.5-uv*cw*0.1)*.1,uv*2.,sin(time*0.058)*0.5+0.5),time);
    //col /= float(S)*2.;
    fragColor = vec4(col,1.);
}
