#define PI 3.14159

void mainImage(out vec4 fragColor, in vec2 fragCoord){
  vec2 v = fragCoord.xy + vec2(0, -0.5 * iResolution.y);
  float border = 20.0 * sin(PI * (v.x - iTime * 120.0) / 50.0);
  float deepNess = step(border, v.y);
  float r;
  float g;
  float b;
  if(deepNess > 0.5){
    r = (110.0 + 140.0 * smoothstep(-20.0, 200.0, v.y)) / 250.0;
    g = (120.0 + 130.0 * smoothstep(-20.0, 200.0, v.y)) / 250.0;
    b = (210.0 + 40.0 * smoothstep(-20.0, 200.0, v.y)) / 250.0;
  }else{
    r = (50.0 * smoothstep(-200.0, 20.0, v.y)) / 250.0;
    g = (60.0 * smoothstep(-200.0, 20.0, v.y)) / 250.0;
    b = (190.0 * smoothstep(-200.0, 20.0, v.y)) / 250.0;
  }
  fragColor = vec4(r, g, b, 1.0);
}
