// -3 by coyote

#define D(r) smoothstep(-p, p, r - length(U//
void mainImage(out vec4 O, vec2 u) {  O-=O; O.a++;
    for (float v, S=.577, p=9./iResolution.x,
         i=0.; i<3.;  ) {
        vec2 U = u*p - i*vec2(.5,.28), a = 33. * O.zw;
        mod( U.y *= S+S , 2.) > 1. ? U.x += .5 : v;
        U = fract(U) - S;
        U.y /= S+S;
        for ( v = D(.4))); a.x < 5.;  a += 2.1 )
             v -= D(.41)+sin(a)*S));
        O[int(i++)] = v * D(.39)));
   }
}
