float PI = 3.14159265359;
float coor[6];
float result[6];
float weight[36];
float bias[6];

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    coor[0] = 2.*fragCoord.x/iResolution.x-1.;
    coor[1] = 2.*fragCoord.y/iResolution.y-1.;
    coor[2] = cos(coor[0]*PI);
    coor[3] = cos(coor[1]*PI);
    coor[4] = sin(coor[0]*PI);
    coor[5] = sin(coor[1]*PI);

    for( int g = 0; g < 6; g++ ){
        for( int h = 0; h < 6; h++ ){
            weight[g*6+h] = 3.3*sin(iTime*pow(.9,float(g))*pow(.85,float(h)));
        }
    }

    for( int g = 0; g < 6; g++ ){
        bias[g] = sin(iTime*pow(.8,float(g)));
    }

    for( int g = 0; g < 6; g++ ){
        for( int h = 0; h < 6; h++ ){
        	result[h] = 0.;
    	}
        for( int h = 0; h < 6; h++ ){
            for( int i = 0; i < 6; i++ ){
            	result[i] += weight[h*6+i]*coor[h];
            }
        }
        for( int h = 0; h < 6; h++ ){
            result[h] += bias[h];
            result[h] = 1./(1.+2.*exp(2.3*result[h]));
            coor[h] = result[h];
        }
    }

    vec3 col = vec3(coor[0],coor[1],coor[2]);
    fragColor = vec4(col,1.0);
}
