void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 color = vec3(
        cos(iTime) * 0.5,
        sin(iTime) * 0.5,
        1.0
    );
    vec3 buffer = texture(iChannel0, uv, 0.0).rgb;
    buffer *= 0.95;
    vec2 p = vec2(
        .5 + sin(iTime * 2.0) * 0.3, 
        .5 + cos(iTime * 6.0) * 0.3
    );
    //float c = circle(p, 0.2 );

    float d = (length(uv.xy - p) - 0.05)*20.0;
    float t = clamp(d, 0.0, 1.0);
    buffer = mix(buffer, color, 1.0-t);
    fragColor = vec4(buffer, 1.0);
}
