vec2 rot(vec2 p, float a) {
    float ca = cos(a);
    float sa = sin(a);
	return vec2(p.x * ca - p.y * sa, p.x * sa + p.y * ca);
}
float f(vec2 fc, float r, float a, float b, vec2 s) {
	return 1.-smoothstep(r-(b+1.), r-b, distance(mod(rot(fc - iResolution.xy * 0.5, a) + s + iResolution.xy * 0.5, vec2(2.*r)), vec2(r)));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float d1 = f(fragCoord, 8.7, 0.5, 2., vec2(iTime * -50.0, 0.0));
    float d2 = f(fragCoord, 8., cos(iTime *  0.2), 1., vec2(23.*iTime, 8.*sin(iTime * 0.8)));
    fragColor = vec4(d1 * d2);
}
