float circleSDF(vec2 uv, float time){
    uv += sin(time + uv *10.) * 0.01;
    return length(uv);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float ghost = 0.;
    for(int i =0; i < 20; i++){
        float ni = float(i)/20.;
        float sdf = circleSDF(uv, iTime + float(ni) * 10.);
        ghost += step(
        abs(sdf - 0.25 - ni * 0.1 ),0.05  - ni * 0.1) * ni;

    }
    // Output to screen

    col = 1.-vec3(ghost);
    fragColor = vec4(col,1.0);
}
