void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x = uv.x * iResolution.x / iResolution.y;
    float cx = iResolution.x / (iResolution.y * 2.);
    vec2 center = vec2(cx,0.5);
    float dist = distance(uv, center);

    float sd = 3. * sin(.1 + dist/4.)/(dist + 0.15);
    float cd = 2. * cos(.1 + dist/4.)/(dist + 0.15);

    float rOff = (1. + sin(iTime)) * 0.2;
    float gOff = (1. + cos(iTime * 1.7)) * 0.3;
    float bOff = (1. + sin(iTime * 0.6)) * 0.1;

    vec3 col = 0.5 + 0.5*vec3(rOff + sin(0.6 * iTime * sd), gOff + cos(1.1 * iTime * cd), bOff + sin(cd * sd * 0.8 * iTime));

    fragColor = vec4(col,1.0);
}
