void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // 画面比改造	
   	//vec2 uv =  2.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;
	vec2 uv  = (2. * fragCoord - iResolution.xy) / iResolution.y;
    //vec2 uv = fragCoord + fragCoord - iResolution.xy;
   	// 座標と原点のラジアン
    float radian = atan(uv.y,uv.x);
    // ラジアンに増やしたい花びらの分だけ掛けることでcos派のくねくねを増やす
    // absにすることで　cos派のマイナス部分をプラスに変える
    float f = (cos(radian *3.));

    //距離を求める
 	float r = length(uv);

    // fragColor += ( abs(f)- r / iResolution.y );
    //vec2 R = iResolution.xy;
  	// fragColor += ( abs( R.x = cos( 3.* atan(uv.y,uv.x) )) - length(uv)/R.y ) / fwidth(R.x);
    // 角度(0-1)原点から距離(0-1)で比較して、外に行くほど許容角度を狭くなる
    //vec3 col = vec3(step(r ,f));
    fragColor = vec4(smoothstep(0., 1.3*fwidth(f), abs(f)-r));
    // Output to screen
    //fragColor = vec4(col,1.0);
}
