void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  vec2 t = fragCoord/iResolution.y - .5;

  float x = iTime*.1;
  float zoom = 10.;
  float rad = .5*(sin((x-.25)*3.1416*2.)+1.)/2.;
  rad = -rad*rad;
  t = mat2(cos(rad), sin(rad), -sin(rad), cos(rad))*t;
  zoom += 3.*sin((x-.25)*3.1416*2.);
  t *= zoom;
  float move = (sin((fract(x)-.5)*3.1416)+1.)/2.+floor(x) + x*.4;
  t += vec2(move*50., -rad*40.);

  t.x = t.x + mod(floor(t.y), 2.);
  float enable = mod(floor(t.x), 2.);
  t = fract(t)-.5;
  float r = zoom*1.*1.5/iResolution.y;
  float w = .45;
  float c = enable*(smoothstep(w+r, w-r, t.x) * smoothstep(w+r, w-r, abs(t.y)));
  c += (1. - enable)*(smoothstep(-w-r, -w+r, t.x) * smoothstep(w+r, w-r, abs(t.y)));
  vec3 col = mix(vec3(.4), vec3(.81, .45, .33), c);
  fragColor = vec4(col, 1.);

}
