void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 xy = (fragCoord * 2. - iResolution.xy) / iResolution.y;
    vec2 center = (iResolution / 2.).xy;
	float eye_size = iResolution.y / 4.;
    float eye_border = eye_size / 10.;
    vec2 eye_pos = vec2(eye_size * 1.5, 0);
    vec2 eye_pos_l = center - eye_pos;
    vec2 eye_pos_r = center + eye_pos;

    fragColor = vec4(0.8, 0.8, 0, 1);
    float d_l = distance(eye_pos_l, fragCoord);
    float d_r = distance(eye_pos_r, fragCoord);
    if (d_l < (eye_size + eye_border) || d_r < (eye_size + eye_border))
        if (d_l > eye_size && d_r > eye_size)
        	fragColor = vec4(0);
        else
			fragColor = vec4(1);

    vec2 eye_ball_l = eye_pos_l - ((eye_pos_l - iMouse.xy) / iResolution.xy) * (eye_size - eye_border) / 1.2;
    if (distance(eye_ball_l, fragCoord.xy) < eye_size / 3.)
        fragColor = vec4(0);

    vec2 eye_ball_r = eye_pos_r - ((eye_pos_r - iMouse.xy) / iResolution.xy) * (eye_size - eye_border) / 1.2;
    if (distance(eye_ball_r, fragCoord.xy) < eye_size / 3.)
        fragColor = vec4(0);
}
