void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy/iResolution.xy;

    float c = pow(abs(0.5 - uv.x), 2.0);

    //float t = ((sin(iTime) + 1.0) / 2.0) * 0.2 + 1.0;
    float t = (sin(iTime / 2.0) + 1.0) / 2.0 + 1.0;
    float hl = sin(uv.x * 20.0 + t + cos(uv.y * (sin(uv.x + uv.y) * 60.0) + t));
    float vl = sin(uv.y * 30.0 * t + (t * 30.0));

    // Output to screen
    fragColor = vec4(hl * vl, vl * 0.05, 0.0, 1.0);
}
