// About Animation CPU:
// https://twitter.com/acpustudio
// 

// +shaven 
#ifdef GL_ES
precision highp float;
#endif

#define resolution iResolution 
#define time iTime

// Based on: http://glslsandbox.com/e#59780.1
//uniform float time;
//uniform vec2 resolution;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 sp = (fragCoord.xy * 2.0 - resolution.xy) / min(resolution.x, resolution.y);
  sp.y *= dot(sp,sp);
  float color = 0.0;
  for (int i = 0; i < 32; i++)
  {
    float t = float(i)+sin(time*0.6+float(i));
    color += 0.05/distance(sp,vec2(sp.x,sin(t+sp.x)));
  }
  fragColor = vec4(color * vec3(.0+sin(iTime*0.5)*0.025+0.025, 0.05+sin(iTime*1.1)*0.025+0.025, 0.05+sin(iTime*.72)*0.025+0.025), 1.0);
  for (int j = 0; j < 21; j++)
    fragColor += textureLod(iChannel0,fragCoord.xy/110.+float(j)*.15*sin(iTime*0.01),0.).grba*.053;
}
