void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

#define t iTime
#define res iResolution.xy


void mainImage( out vec4 o, in vec2 FC )
{
#define S sin(40.*length(FC/res-.5))
#define ROOF_START step(-.5,-S)*.1
#define ROOF_LEFT_STOP step(.5,S)*.1
#define ROOF_RIGHT_STOP step(.5,S)*.1
#define ROOF_WALL_START .0001
#define COL transpose(mat3(\
    .2,0,1,\
    1,.92,.0,\
    0,1,1))

#define rnd(x) fract(sin(x*1e2)*1e2)
#define T(dd) (texelFetch(iChannel0,ivec2(FC.xy+vec2(dd,-1))%int(res),0).rgb*inverse(COL))
#define lr T(-1).r>.5
#define cr T( 0).r>.5
#define rr T( 1).r>.5
#define lg T(-1).g>.5
#define cg T( 0).g>.5
#define rg T( 1).g>.5
#define lb T(-1).b>.5
#define cb T( 0).b>.5
#define rb T( 1).b>.5
    if (iFrame == 0) o = vec4(0);
    if(t<.1)if(rnd(length(FC.xy))<.5)o.r+=1.;else o.b+=1.;
    else if(lb&&cr&&rr&&rnd(length(FC.xy))<ROOF_START)o.g+=1.;
    else if(lg&&cg&&rr&&rnd(length(FC.xy))<ROOF_RIGHT_STOP)o.b+=1.;
    else if(lg&&cr&&rr&&rnd(length(FC.xy+vec2(-1,0)))<ROOF_RIGHT_STOP)o.b+=1.;
    else if(lb&&cg&&rg&&rnd(length(FC.xy))<ROOF_LEFT_STOP)o.r+=1.;
    else if(lb&&cb&&rg&&rnd(length(FC.xy+vec2(1,0)))<ROOF_LEFT_STOP)o.r+=1.;
    else if(lg&&cg&&rg&&rnd(length(FC.xy))<ROOF_WALL_START)o.b+=1.;
    else if(lb&&cg&&rnd(length(FC.xy+vec2(-1)))<ROOF_WALL_START)o.r+=1.;
    else if(cb)
      if(rg)
        if(lg) o.b+=1.;
        else o.g+=1.;
      else o.b+=1.;
    else 
      if(cr)
        if(lg)
          if(rg) o.r+=1.;
          else o.g+=1.;
        else o.r+=1.;
      else
        if(lr) o.r+=1.;
        else
          if(lg)
            if(rb) o.b+=1.;
            else o.g+=1.;
          else o.g+=1.;
    o.rgb *= COL;
}
