//#define hires //for high res monitors
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
vec2 uv=(fragCoord/vec2(iResolution))-vec2(0.5);
vec2 andr=abs(uv);
float val1,val2;
val1=0.3;
#ifndef hires
val2=0.001;
#else
val2=0.003;
#endif
if(max(andr.x-val1,andr.y)<val2){
fragColor= vec4(.0,.0,.0,1.);
}else{
int vv=int(fragCoord.y);
#ifndef hires
if(vv%4<=1){
#else
if(vv%8<=3){
#endif
fragColor= vec4(.7,.7,.7,1.);
}else{
fragColor = vec4(1.,1.,1.,1.);
}
}
}
