//Basic Fractal Sin by @paulofalcao

const int maxIterations=8;

//generic rotation formula
vec2 rot(vec2 uv,float a){
    float c=cos(a);float s=sin(a);
    return uv*mat2(c,-s,s,c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	//normalize stuff
	vec2 uv=iResolution.xy;uv=-.5*(uv-2.0*fragCoord.xy)/uv.x;

	//global zoom
	uv*=sin(iTime)*2.5+5.5;

	//shift, mirror, rotate and scale 6 times...
	for(int i=0;i<maxIterations;i++){
        uv*=1.5;                        //<-Scale
        uv=rot(uv,iTime);               //<-Rotate
        uv+=sin(uv*sin(iTime)*8.0)*0.1; //<-Sin Distortion
		uv=abs(uv);                     //<-Mirror
        uv-=0.5;                        //<-Shift
	}

	//draw a circle
	float c=length(uv)>0.2?0.0:1.0;	

	fragColor = vec4(c,c,c,1.0);
}
