void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - .5 * iResolution.xy) / iResolution.y;

  	float n = (cos(iTime * .2) * 1.5 + 2.);
  	uv = fract(uv * n) - .5;

  	float t = sin(iTime * 1.5) * .05 + .05;
  	float r = length(uv);

  	vec3 color = vec3(0.);
  	color += smoothstep(.1, .8, cos(dot(uv.y * uv.x, uv.x) * 2. + iTime * .2)) * 1.5;

  	color -= cos(cos(uv.x * uv.y * 25.) * 15. - iTime) * 3.5;
  	color = mix(color, vec3(.3, .4, .8) * 1.3, r + .55 - t * 3.5) * 2.2;

  	fragColor = vec4(color, 1.);
}
