vec3 f(vec3 a, vec3 b)
{
 	return mix(a, b, abs(sin(iTime)));   
}

vec3 multiply(vec3 a, vec3 b)
{
    return a*b;
}

float magic(float i, float j)
{
	return 1.0-(1.0-i)*(1.0-j);
}

vec3 screen(vec3 a, vec3 b)
{   
    vec3 one = vec3(1.0);
    return one-(one-a)*(one-b);
  	//return 1.0-(1.0-a)*(1.0-b);
    //return vec3 (magic(a.x,b.x),magic(a.y,b.y),magic(a.z,b.z));
}

vec3 madness(vec3 a, vec3 b)
{
	return f(multiply(a,b),screen(a,b));   
}

vec3 Overlay(vec3 a, vec3 b)
{
    float grey = (b.x+b.y+b.z) / 3.0;
	return mix(2.0*multiply(a,b), 1.0-2.0*(1.0-a)*(1.0-b), step(0.5,grey));
}

vec3 flimmerkasten(vec3 a, vec3 b, vec2 uv)
{
    return mix(a,b,abs(sin(iTime*iTime*uv.x*uv.y)));
}


