// Fork of "Convolution DFT" by wyatt. https://shadertoy.com/view/tsSfzc
// 2020-08-11 18:39:44

Main {
    float l = length(A(mod(R/R.y*(U-0.4*R),R)).xy);
	Q = sin(vec4(5.*l)*(1.+.2*vec4(1,2,3,4)));
}

vec2 a (vec2 U ) {
    if (length(U-0.2*R)>50.) return vec2(0);

	return vec2(A(U).x,0);
}
Main {
	Q = vec4(0);
    vec2 w = 2.*pi*(U-0.5)/R;
    for (float i = 0.; i < R.y; i++) {
        Q.xy += a(vec2(U.x,i))*e(-(w.y-pi)*i)/sqrt(R.y);
    }
}

#define R (iResolution.xy)
#define A(U) texelFetch(iChannel0,ivec2(U),0)
#define B(U) texelFetch(iChannel1,ivec2(U),0)
#define pi 3.14159265359
mat2 e (float t) {
    float c = cos(t),s = sin(t);
	return mat2(c,-s,s,c);
}
#define Main void mainImage( out vec4 Q, in vec2 U )

Main {
	Q = vec4(0);
    vec2 w = 2.*pi*(U-0.5)/R;
    for (float i = 0.; i < R.x; i++) {
        Q.xy += A(vec2(i,U.y)).xy*e(-(w.x-pi)*i)/sqrt(R.x);
    }
    float d = 2.;
    float n = max(2.+1.*sin(.666*iTime),1.);

    for(float i = 1.; i < 5.; i++){
        vec2 w = vec2(0);

        for (float j = 0.; j < 15.; j++) {
            if (j >= n) break;
            float a = d*dot(U-0.5*R,vec2(0,1)*e(j*6.2/n+iTime*sqrt(i)));
        	w += vec2(0,1)*e(a);
        }  
        Q.xy = mat2(Q.xy,-Q.y,Q.x)*w;
        d*=.35+.5/n;
        Q*=.35+.5/n;
    }
    if (length(U-0.5*R)>0.5*R.y) Q*=0.;
}

Main {
	Q = vec4(0);
    vec2 w = 2.*pi*(U-0.5)/R;
    for (float i = 0.; i < R.x; i++) {
        Q.xy += A(vec2(i,U.y)).xy*e(w.x*i)/sqrt(R.x);
    }
}

Main {
	Q = vec4(0);
    vec2 w = 2.*pi*(U-0.5)/R;
    for (float i = 0.; i < R.y; i++) {
        Q.xy += A(vec2(U.x,i)).xy*e(w.y*i)/sqrt(R.y);
   }
}
