void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float RADIUS=min(iResolution.x,iResolution.y)*0.5;
    float col=distance(iResolution.xy*0.5,fragCoord);
    if(col>RADIUS)
        col=abs(iResolution.x*0.5-fragCoord.x);
    col = (round(col*0.1)+round(iTime*10.0))*10.0;
    vec3 pos = vec3(col*.3) + col * 500. + 50.0;
    pos = fract(pos * vec3(.1031, .1030, .0973));
    pos += dot(pos, pos.yxz+33.33);
    fragColor = vec4(fract((pos.xxy + pos.yxx)*pos.zyx).rgb, 1.0);
}
