void mainImage( out vec4 o, in vec2 FC )
{
    vec3 u=normalize(vec3(2.*FC-iResolution.xy,iResolution.xy.y))*1.2;
    for(float i=0.;i<4.;i++)
        u.y+=cos(u.y*i*5.)*.1,
        u.z+=sin(u.x*u.x+u.y*u.y-iTime*.3),
        u+=cos(u*i*7.)*.1,
        o=max(o,cos(3.*dot(u,u)+vec4(.3,.1,0,0)));
}

vec2 mainSound( in int samp,float t){
    return vec2(sin(pow(fract(t*5.)+sin(t*clamp(t,9.,9e9)*sin(t))*.01,
                        mix(.3,1.,smoothstep(0.,.5,sin(t*4.))))*1e3))*.2;}
