const int KEY_LEFT  = 37;
const int KEY_UP    = 38;
const int KEY_RIGHT = 39;
const int KEY_DOWN  = 40;

bool ReadKey( int key, bool toggle )
{
	float keyVal = texture( iChannel0, vec2( (float(key)+.5)/256.0, toggle?.75:.25 ) ).x;
	return (keyVal>.5)?true:false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(uv.x, uv.y, uv.x * uv.y);

    // Check for key up Pressed
    if(ReadKey(KEY_UP, false))
    {
        // if key up is pressed, then white
    	col = vec3(1.);
    }

	// Check for key right pressed
    if(ReadKey(KEY_RIGHT, false))
    {
        // if key up is pressed, then white
    	col = vec3(1., 0.0,0.0);
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
