mat2 rot(float a){
	return mat2(cos(a), sin(a), sin(-a), cos(a));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 res = iResolution.xy;
    vec2 vv = (fragCoord - .5*res)/res.y;
    float t = iTime;

	//uv.x *= 1.666;
	uv -= .5;
	//uv.x -= .333;






    float s = .06; 	//size
    float d = .1; 	//spin delay
    float p = .5; 	//period
    float sp = 1.; 	//speed
    bool invert = false; //roation coming from outside or inside





    for (float i=1.; i<=50.; i++){
		//if (pow(uv.x - .0, 2.)+pow(uv.y - .0, 2.) >= (i-1.)*s && pow(uv.x - .0, 2.)+pow(uv.y - .0, 2.) <= s+(i-1.)*s)
        if (length(vv) >= (i-1.)*s && length(vv) <= s+(i-1.)*s)
        {
            if (invert == false){
                uv *= rot(3.1415*2.*smoothstep(.0, 1., pow(mod((sp*t+i*d)*p, 2.), 2.)));
            }else{
                uv *= rot(3.1415*2.*smoothstep(.0, 1., pow(mod((sp*t-i*d)*p, 2.), 2.)));
            }
		}
    }


	/*if (pow(uv.x - .0, 2.)+pow(uv.y - .0, 2.) <= .015){
		uv *= rot(3.1415*2.*smoothstep(.0, 1., pow(mod(t*.75, 2.), 2.)));
	}
	if (pow(uv.x - .0, 2.)+pow(uv.y - .0, 2.) >= .015 && pow(uv.x - .0, 2.)+pow(uv.y - .0, 2.) <= .1 ){
		uv *= rot(3.1415*2.*smoothstep(.0, 1., pow(mod((t-.05)*.75, 2.), 2.))) ;
	}
	if (pow(uv.x - .0, 2.)+pow(uv.y - .0, 2.) >= .1 && pow(uv.x - .0, 2.)+pow(uv.y - .0, 2.) <= .3 ){
		uv *= rot(3.1415*2.*smoothstep(.0, 1., pow(mod((t-.1)*.75, 2.), 2.))) ;
	}*/

	vec3 col = vec3(.0);

	/*if ((uv.y - .01)*(uv.y + .01) <= .001){
		col += vec3(1.);
	}*/
    fragColor = vec4(texture(iChannel0, uv-.5));
}
