vec3 neonPurple = vec3(99.0/255.0,36.0/255.0,177.0/255.0);
vec4 glowLine(bool horizontal, vec2 uv, vec2 pos, float thickness, float strength) {

    float glow = 0.0;

    if (horizontal) {
        glow = smoothstep(pos.y - thickness, pos.y, uv.y) - smoothstep(pos.y, pos.y + thickness, uv.y);
    } else {
        glow = smoothstep(pos.x - thickness, pos.x, uv.x) - smoothstep(pos.x, pos.x + thickness, uv.x);
    }

   return vec4(neonPurple*glow*strength*(sin(iTime*4.0) + 2.0) , 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = glowLine(false, uv, vec2(0.5), 0.04, 0.7);

    fragColor += glowLine(false, uv, vec2(0.5), 0.008, 0.7);

    fragColor += vec4(neonPurple*(sin(iTime*4.0) *0.7), 1.0);
}
