float SphereSDF(vec3 p)
{
    return length(p) - 1.0;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x += (cos(uv.x) * sin(uv.y)) * sin(iTime);
    uv.y += (cos(uv.y) * sin(uv.x)) * cos(iTime);

    // Time varying pixel color
    vec3 col =sin(iTime * 2.0) +  0.5*cos(iTime*uv.xyx+vec3(0,2,4));
    float ncol = SphereSDF(col);

    // Output to screen

    fragColor = vec4(col.x * sin(ncol*iTime),col.yz *sin(ncol*iTime),ncol);
}
