	const float size = 64.0;
    const float sizeB = size*2.0;
    const vec4 green = vec4(0,1.0,0,1.0);  
	const vec4 zeroAlpha = vec4(0,0,0,0);  





void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

vec4 xCol = vec4(0,0,0,0);
    float xA = iMouse.x + size; 
	float yA = iMouse.y + size; 

    //everything below this range make green
    if (fragCoord.x < xA && fragCoord.y < yA){
    	xCol = green;
    } 

    //everything above this range make 0 alpha
    if (fragCoord.x < xA-sizeB  || fragCoord.y < yA-sizeB){
         xCol = zeroAlpha;
    }

    fragColor = xCol;



}
