const bool vertical = false;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;
    vec4 lines;

    if (vertical) {
        lines = texture(iChannel0, uv.xx);
    } else {
        lines = texture(iChannel0, uv.yy);
    }

    fragColor = vec4(lines);
}
