#define pi 3.14159265359
#define tau 3.14159265359*2.
float speed= 10.;
float wobble = 0.;
float rs = .0;
vec2 rp = vec2(0.5);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    float s = uv.x;//abs(-1. + 2.*uv.x); s *= s;
    //zoom = 10. - (uv-rp).x*2.;
    float zoom = 5.+(s*s*5.);
	float t = iTime;
    mat2 rm = mat2(cos(t*rs),-sin(t*rs),
                   sin(t*rs), cos(t*rs));
    //uv = rm*(uv - rp) + rp;
    float l = sin((uv.x*tau*zoom + t*speed) + cos(uv.y*tau*wobble));
    l = clamp(0.2,1.,l);
	vec3 col = vec3(l);
    //col = vec3(s);
    fragColor = vec4(col,1.0);
}
