void mainImage( out vec4 Q, in vec2 U )
{
    vec4 a = A(U);
    vec4 b = B(U);
    float 
        n = length(A(U+vec2(0,1))),
        e = length(A(U+vec2(1,0))),
        s = length(A(U-vec2(0,1))),
        w = length(A(U-vec2(1,0)));
   	vec3 no = normalize(vec3(e-w,n-s,1));
    Q = atan(length(a))*abs(normalize(a));
}

void mainImage( out vec4 Q, in vec2 U )
{
    Q = A(U);
    Q += B(U);
    if(iMouse.z>0.&&length(U-iMouse.xy)<10.||(length(U-0.5*R)<2.&&iFrame<2)) Q = sin(4.*iTime*vec4(1,2,3,4));
    if (iFrame < 1) Q = vec4(1);
}

void mainImage( out vec4 Q, in vec2 U )
{
    vec4 a = A(U);
    vec4 b = B(U);
    vec4 m = vec4(0);
    vec4 n = vec4(0);
    for (int x = -2; x <= 2; x++)
    for (int y = -2; y <= 2; y++) if (x!=0||y!=0) {
        m += A(U+vec2(x,y));
        n += B(U+vec2(x,y));
    }
    m = m/24.-a;
    float l = length(a);
    if (l>0.) m -= .1*normalize(a);
    n = n/25.-b;
    Q = b+.02*(n)+(m);
    if (iFrame < 1) Q = vec4(0);
}
