void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = fragCoord/iResolution.xy;
	uv.x/=122.;
	uv.y/=32.;
	uv.y-=.015;
	float Y=uv.y*99.,Q=Y,K=14.,U=uv.x*99999./190.-3.,P=U,X=U;

	for(float k=14.;k>0.;k-=1.)
	{
		U=X;
		X=X*X-Y*Y+P;
		Y=2.*U*Y+Q;
		if (X*X<(1.01+sin(iTime)))
			fragColor.r=pow(k,2.)/255.;
	}
}
