float Circle(vec2 uv, vec2 p,  float r, float blur)
{
	float d = length(uv-p);
    float c = smoothstep(r, r-blur, d);    

    return c;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;

    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.);

    vec2 p = vec2(0., 0.);
    float mask = Circle(uv, p, 0.4, 0.003);

    mask -= Circle(uv, p-vec2(0.2, -0.1), 0.07, 0.003);
	mask -= Circle(uv, p-vec2(-0.2, -0.1), 0.07, 0.003);

    float mouth = Circle(uv, p, 0.3, 0.003);
    mouth -= Circle(uv, p-vec2(0.0, -0.1), 0.3, 0.003);
    mask -= mouth;
    //col = vec3(mouth);

    col = vec3(0.,1.,0.) * mask;
    fragColor =vec4(col,1.0);
}
