void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Based on shader by zavie https://www.shadertoy.com/view/XsfXDr
	float ratio = iResolution.x / iResolution.y;
	vec2 uv = vec2(ratio, 1.) * (2. * fragCoord.xy / iResolution.xy - 1.);
    vec3 n = vec3(uv, sqrt(1. - clamp(dot(uv, uv), 0., 1.)));

    // the rest is my code

    // pick A colour
    vec4 col = vec4(0.9,0.2,0.5,1.0);

    if(iMouse.x > 0.5 * iResolution.x){
        col = vec4(0.5,0.9,0.2,1.0);
    } else if(iMouse.x > 0.4 * iResolution.x){
        col = vec4(0.2,0.5,0.9,1.0);
    } else if(iMouse.x > 0.3 * iResolution.x){
        col = vec4(0.9,0.5,0.2,1.0);
    } else if(iMouse.x > 0.2 * iResolution.x){
        col = vec4(0.7,0.6,0.5,1.0);
    }


    // light it
    vec3 light = vec3(sin(iTime)*2.0,0.6,1.0);
    if(n.z != 0.0) {
        float d = dot(light,n);
        // log
        col *= max(0.15,log(d+1.2)*0.8+0.7);
        // linear
        //col *= max(0.15,(d+1.2)*0.8+0.7);
    } else {
        // Background is black
        col = vec4(0.0,0.0,0.0,1.0);
    }


	fragColor = col;
}
