void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    uv += vec2(tan(mod(uv.x,uv.y)+iTime)/4.,sin(mod(uv.y,uv.x)+iTime)/2.);

    uv = fract(uv * 2.5)-.5;
    float a = iTime*2.;
    uv *= mat2(cos(a),-sin(a),sin(a),cos(a));



    float w = abs(sin(iTime*.5)/4.)+.2;
    float s = step(uv.x,-w)-step(uv.x,w);
    s *= step(uv.y,-w)-step(uv.y,w);

    vec3 col = vec3(s,s/4.,s/2.);


    fragColor = vec4(col,1.0);
}
