//
// Started from here:
// https://www.shadertoy.com/view/WlX3Wr
//
// Then gone too experimental :D
//


#define RADIUS  0.1
#define TAIL_LENGTH 0.8
#define H_SIZE 0.3
#define V_SIZE 0.3
#define H_OSC 15.
#define V_OSC 5.

#define BG_COLOR    vec3(0.165, 0.125, 0.224)
#define SNAKE_COLOR vec3(0.906, 0.275, 0.6)


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv    = fragCoord/iResolution.xy;
    vec2 uvUni = (fragCoord - .5*iResolution.xy) / iResolution.y;
    vec2 pivot = vec2(0.);

    vec2 pos;
    float step = H_SIZE * 0.001;
    for(float t = TAIL_LENGTH; t > 0.; t -= step)
    {
        pos = pivot;
        pos.x += sin(iTime + t*H_OSC) * H_SIZE;
        pos.y += cos(iTime + t*V_OSC) * V_SIZE;

		if(length(pos - uvUni) <= RADIUS)
        {   
            fragColor = vec4(SNAKE_COLOR * (t / TAIL_LENGTH), 1.0);
            return;
        }
    }

    fragColor = vec4(BG_COLOR, 1.);
}
