#define TAU 6.28318
#define PI 3.141592

mat2 Rot(float a){
    float s = sin(a), c=cos(a);
    return mat2(c,-s,s,c);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


	vec4 area = vec4(1.5,-1.5,10.,10.);
    float t = iTime*.0032;
    vec2 c = ((fragCoord.yx/iResolution.xy))*area.zw*.5+.5;
    c.xy *= Rot(t);
    vec2 gv = c *.5396;
    gv /= iTime*3.213;



    vec2 z;
    float iter;
    for(iter=0.; iter < 255.; iter++){
        z = vec2(z.x*z.x-z.y*z.y, 2.*z.x*z.y) + gv *.35+.35;

        if (length(z)>7.) break;
    }

   	float m= sqrt(iter/255.); 

    vec3 col= sin(vec3(.34,.046,.075)*m*20.)*.5+.5;

	col *=mix(texture(iChannel0, vec2(c.y-iTime*.912,z+iTime*.021)/iter).rgb, col, .5)*fract(2345.2)*TAU;

    fragColor = vec4(col,1.0);
}
